void drawAxis(bool hasAxis) {
    printf("\n");
    for (int i = 0; i < Y_AXIS_SIZE; i++) {
        for (int j = 0; j < X_AXIS_SIZE + 1; j++) {
            if (!hasAxis && (axis[i][j] == '-' || axis[i][j] == '|'))
                printf(" ");
            else
                printf("%c", axis[i][j]);
        }
        printf("\n");
    }
    printf("\n");
}

void clearAxis() {
    for (int i = 0; i < Y_AXIS_SIZE; i++) {
        for (int j = 0; j < X_AXIS_SIZE + 1; j++) {
            if (axis[i][j] == '*') {
                if (i == 12 && j != 40) {
                    axis[i][j] = '-';
                } else if (j == 40 && i != 12) {
                    axis[i][j] = '|';
                } else if (i == 12 && j == 40) {
                    axis[i][j] = '+';
                } else {
                    axis[i][j] = ' ';
                }
            }
        }
    }
}